mapper.select:
        SELECT * from paper
        WHERE true

            and title like concat('%',#{name},'%')


            and uid = #{uid}


            and template = #{template}

#         根据填写次数排序
        order by access_count desc

            limit #{page}, #{pageSize}

SELECT count(1) from paper
WHERE true

    and title like concat('%',#{name},'%')


    and uid = #{uid}


    and template = #{template}

order by create_time desc
